CREATE TABLE "COMPONENT_APP_LOGS" 
   (    
    "LOG_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOT NULL ENABLE, 
    "THREAD_NAME" VARCHAR2(100), 
    "LOG_DATE" DATE NOT NULL ENABLE, 
    "LOGGER" VARCHAR2(250) NOT NULL ENABLE, 
    "LOG_LEVEL" VARCHAR2(10) NOT NULL ENABLE, 
    "MESSAGE" CLOB NOT NULL ENABLE, 
    "TIME" VARCHAR2(25), 
    "FILE_VALUE" VARCHAR2(500), 
    "CORRELATION_ID" VARCHAR2(50), 
     PRIMARY KEY ("LOG_ID")
   );

